mapper.select:
select * from t_sale_list


        and saleNumber like #{saleNumber}


        and customerId=#{customer.id}


        and state=#{state}


        and date_format(saleDate,'%Y-%m-%d %H:%i:%S') >= date_format(#{bSaleDate},'%Y-%m-%d %H:%i:%S')


        and date_format(saleDate,'%Y-%m-%d %H:%i:%S') <= date_format(#{eSaleDate},'%Y-%m-%d %H:%i:%S')


order by saleDate desc
